function addFriendship(e){let t=$(" .friend-request-btn",e),o=$(t).prop("href");t.click((function(e){e.preventDefault(),$.ajax({type:"get",url:o,success:function(e){console.log(e.data.friend_id),$("#person-"+e.data.friend_id).remove(),new Noty({theme:"relax",type:"success",text:"Friend Added",layout:"topRight",timeout:1e3}).show()},error:function(e){console.log(e.responseText)}})}))}let people=$(".individual");for(let e of people)addFriendship(e);